import pymysql


'''定义一个执行sql语句的函数，只需传入sql语句即可,前提是确定好数据库的名称和'''
def execute_sql(sql):
    conn=pymysql.connect(
        host='127.0.0.1',
        port=3306,
        user='root',
        password='1234',
        db='flask_book',
        charset='utf8'
    )
    cursor=conn.cursor()
    sql=sql

    try:
        cursor.execute(sql)
        conn.commit()
    except:
        print('保存有问题。。。')
        conn.rollback()
    cursor.close()
    conn.close()
